{
  "index_patterns": ["game_record_*"],  // 匹配所有 game_record_ 开头的索引
  "template": {
    "mappings": {
      "properties": {
        "id": {
          "type": "keyword"
        },
        "user_id": {
          "type": "long"
        },
        "user_name": {
          "type": "keyword"
        },
        "user_ip": {
          "type": "ip"
        },
        "user_platform": {
          "type": "keyword"
        },
        "bet_txn_id": {
          "type": "keyword"
        },
        "bet_amount": {
          "type": "scaled_float",
          "scaling_factor": 100
        },
        "settled_txn_id": {
          "type": "keyword"
        },
        "settled_amount": {
          "type": "scaled_float",
          "scaling_factor": 100
        },
        "refund_txn_id": {
          "type": "keyword"
        },
        "refund_amount": {
          "type": "scaled_float",
          "scaling_factor": 100
        },
        "net_amount": {
          "type": "scaled_float",
          "scaling_factor": 100
        },
        "valid_bet_amount": {
          "type": "scaled_float",
          "scaling_factor": 100
        },
        "currency": {
          "type": "keyword"
        },
        "exchange_rate": {
          "type": "scaled_float",
          "scaling_factor": 100
        },
        "bet_amount_usd": {
          "type": "scaled_float",
          "scaling_factor": 100
        },
        "settled_amount_usd": {
          "type": "scaled_float",
          "scaling_factor": 100
        },
        "refund_amount_usd": {
          "type": "scaled_float",
          "scaling_factor": 100
        },
        "valid_bet_amount_usd": {
          "type": "scaled_float",
          "scaling_factor": 100
        },
        "net_amount_usd": {
          "type": "scaled_float",
          "scaling_factor": 100
        },
        "game_code": {
          "type": "keyword"
        },
        "game_name": {
          "type": "keyword"
        },
        "game_type": {
          "type": "keyword"
        },
        "game_ext_name": {
          "type": "keyword"
        },
        "intergrator_code": {
          "type": "keyword"
        },
        "venue_code": {
          "type": "keyword"
        },
        "venue_name": {
          "type": "keyword"
        },
        "round_id": {
          "type": "keyword"
        },
        "status": {
          "type": "keyword"
        },
        "bet_at": {
          "type": "date",
          "format": "epoch_millis"
        },
        "settled_at": {
          "type": "date",
          "format": "epoch_millis"
        },
        "refund_at": {
          "type": "date",
          "format": "epoch_millis"
        },
        "cancel_at": {
          "type": "date",
          "format": "epoch_millis"
        },
        "created_at": {
          "type": "date",
          "format": "epoch_millis"
        },
        "updated_at": {
          "type": "date",
          "format": "epoch_millis"
        },
        "created_at_iso": {
          "type": "date",
          "format": "strict_date_optional_time||strict_date_time"
        },
        "updated_at_iso": {
          "type": "date",
          "format": "strict_date_optional_time||strict_date_time"
        }
      }
    }
  }
}

{
  "index_patterns": ["game_record*"],  // 匹配 Logstash 的动态索引名
  "template": {
    "settings": {
      "number_of_shards": 1,                // 根据数据量调整分片数
      "number_of_replicas": 1               // 副本数
    },
    "mappings": {
      "properties": {
        "order_id": {
          "type": "keyword"                 // 唯一标识符，适合使用 keyword
        },
        "customer_name": {
          "type": "text",
          "fields": {
            "keyword": {
              "type": "keyword",
              "ignore_above": 256           // 支持聚合的字段
            }
          }
        },
        "amount": {
          "type": "float"                   // 金额字段
        },
        "timestamp": {
          "type": "date"                    // 时间字段
        }
      }
    }
  },
  "priority": 100                            // 优先级，确保此模板生效
}
